markup.content:
mixin listitem
	li.c-on-page-menu__item
		a(href='#')
			span Hier steht so zeug mit noch viel mehr text #{n++}

- var n = 0;

div
	ul.c-on-page-menu
		while n < 4
			+listitem #{n++}


div
	ul.c-on-page-menu
		while n < 50
			+listitem #{n++}

style.content:
$unordered-list-items-margin-bottom:  15px !default;
$ku-blue: #232F66 !default;
$main-nav-list__item--roadmap-font-color: #757575 !default;
$white: #FFFFFF;

body {
	margin: auto;
	max-width: 1200px;
}

div {
	display: flex;
	justify-content: center;
}

.c-on-page-menu {
    padding: 0;
    list-style-type: none;

    &__item {
        position: relative;
        padding-left: 20px;
        padding-bottom: $unordered-list-items-margin-bottom;
        margin-bottom: 0;

        a {
            text-decoration: none;

            &:hover {
                color: $ku-blue;
            }
        }

        &::before {
            position: absolute;
            top: 4px;
            left: 1px;
            content: " ";
            display: block;
            width: 16px;
            height: 16px;
            border-radius: 50%;
            background-color: $main-nav-list__item--roadmap-font-color;
            // border: 3px solid $white;
            z-index: 10;
            transition: background-color .3s;
        }

        &::after {
            content: " ";
            position: absolute;
            top: 22px;
            left: 6px;
            width: 1px;
            height: calc(100% - 22px);
            // border-left: 4px dotted $main-nav-list__item--roadmap-font-color;
            z-index: 5;
        }

        &:last-child::after {
            display: none;
        }
    }

    @media (min-width: 768px) {
        display: flex;
        flex-wrap: wrap;

        &__item {
            overflow: hidden;
            text-align: center;
            hyphens: auto;
            width: 140px;
            padding: 20px 9px 0 9px;
						margin: 0 2px 2rem;

            &::before {
                top: 0;
                left: 50%;
								transform: translatex(-50%);
            }

            &::after {
                top: 6px;
                left: 0;
                width: calc(100% + 0.2rem);
                height: 4px;
                // border-top: 4px dotted $main-nav-list__item--roadmap-font-color;
								background-image: url("data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSI5IiBoZWlnaHQ9IjQiPjxjaXJjbGUgY3g9IjIiIGN5PSIyIiByPSIyIiBmaWxsPSJyZWQiIGZpbGwtcnVsZT0iZXZlbm9kZCIvPjwvc3ZnPg==");
							background-size: 9px 4px;
							background-position-y: 0;
            }

            &:first-child::after {
                left: 50%;
                width: calc(50% + 0.2rem);
            }

            &:last-child::after {
                display: block;
                left: 0;
                width: calc(50% + 0.2rem);
            }
        }
    }
}
