style.content:
$color1: #A5C85A;
$color2: #E0E0E0;

.c-pagination {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  list-style-type: none;
  padding: 0;

  &__item {
    position: absolute;
    top: -9999px;
    left: -9999px;

    &:hover {

      &:not([class*='c-pagination__item--current']) {
        border-bottom: 2px solid $color2;
      }
    }

    &--current {
      border-bottom: 2px solid $color1;
    }

    &--besides,
    &--current {
      position: initial;
      top: initial;
      left: initial;
    }
  }

  &__link {
    display: block;
    color: currentcolor;
    text-decoration: none;
    padding: 0.5rem 1rem;
    line-height: 1.5rem;

    &:hover {
      text-decoration: none;
    }

    &:focus,
    &:active {
      box-shadow: 0 0 4px 2px rgba(0 0 0 / 5%);
    }

    .c-pagination__item--besides &,
    .c-pagination__item--skipall & {
      line-height: 0;
    }
  }

  &__arrow {

    &--right {
      transform: rotate(180deg);
    }
  }

  @media only screen and (min-width: 400px) {

    &__item {

      &--skipall {
        position: initial;
        top: initial;
        left: initial;
      }
    }
  }

  @media only screen and (min-width: 768px) {

    &__item {
      position: initial;
      top: initial;
      left: initial;
    }
  }
}
