markup.content:
mixin carousel-slider(data)
	// Start - carousel Slider
	section.c-slider.c-slider--carousel.splide(data-slider="carousel")
		.splide__track
			ul.splide__list
				each slide in data
					// Start - Slider Item
					li.splide__slide.c-slider__slide
						// Start - Slider content
						.c-slider__slide-inner-carousel
							img(alt="alternative Text" width="800" height="800" src=`https://picsum.photos/id/${slide.imageID}/800/800.webp`)
						// End - Slider content
					// End - Slider Item
		.c-slider__arrows.splide__arrows
			button.c-slider__arrow.c-slider__arrow--prev.splide__arrow.splide__arrow--prev
				svg.c-slider__prev-icon(xmlns='http://www.w3.org/2000/svg' width='24' height='24' viewBox='0 0 24 24')
					path(d='M7.33 24l-2.83-2.829 9.339-9.175-9.339-9.167 2.83-2.829 12.17 11.996z')
			button.c-slider__arrow.c-slider__arrow--next.splide__arrow.splide__arrow--next
				svg(xmlns='http://www.w3.org/2000/svg' width='24' height='24' viewBox='0 0 24 24')
					path(d='M7.33 24l-2.83-2.829 9.339-9.175-9.339-9.167 2.83-2.829 12.17 11.996z')
	// End - carousel Slider

.container
	+carousel-slider([
		{
			imageID: 18,
		},
		{
			imageID: 19,
		},
		{
			imageID: 25,
		},
		{
			imageID: 28,
		},
		{
			imageID: 51,
		},
	])

style.content:
:root {
  --transition-default: 0.3s ease-in-out;
  --slider-dot-size: 1rem;
  --slider-carousel-lvl2-scale: scale(1.3);
  --slider-carousel-lvl1-scale: scale(1.6);
  --slider-carousel-height: 100cqi;
  --neutral-color-base: #000000;
  --neutral-color-inverted: #FFFFFF;
  --primary-color: red;
}

.c-slider {

  $this: &;

  container-type: inline-size;

  @media all and (min-width: 640px) {
    --slider-carousel-height: 40cqi;
  }

  &__slide {
    height: var(--slider-carousel-height);
    display: flex;
    align-items: center;

    &.is-active {
      z-index: 3;
    }

    &.is-prev,
    &.is-next {
      z-index: 2;
    }

    &.is-prev-prev,
    &.is-next-next {
      z-index: 1;
    }
  }

  &__slide-inner-carousel {
    background-color: var(--neutral-color-inverted);
    transition: transform var(--transition-default), opacity var(--transition-default);
    transform: scale(1);

    > * {
      #{$this}__slide.is-prev-prev &,
      #{$this}__slide.is-next-next & {
        opacity: 0.2;
      }

      #{$this}__slide.is-prev &,
      #{$this}__slide.is-next & {
        opacity: 0.7;
      }

			#{$this}__slide:not(.is-active):not(.is-prev):not(.is-next) & {
        opacity: 0.2;
      }
    }

    #{$this}__slide.is-prev & {
      transform: var(--slider-carousel-lvl2-scale);
    }

    #{$this}__slide.is-next & {
      transform: var(--slider-carousel-lvl2-scale);
    }

    #{$this}__slide.is-active & {
      transform: var(--slider-carousel-lvl1-scale);
    }
  }

  &__arrows {
    display: flex;
    justify-content: center;
  }

  &__arrow {
    line-height: 0;
  }

  &__prev-icon {
    transform: rotate(180deg);
  }
}

// Defaults

*, *:before, *:after {
  box-sizing: border-box;
}

img, object, embed, video {
  height: auto;
  max-width: 100%;
}

.container {
	max-inline-size: 1200px;
	margin-inline: auto;
}

script.content:
function removeClasses(sliderList) {
	[...sliderList].forEach(function(slide) {
		slide.classList.remove('is-prev-prev', 'is-prev', 'is-active', 'is-next', 'is-next-next');
	});
}

function getSlides(carouselSlider) {
	return {
		prevPrev: carouselSlider.Components.Slides.getAt(carouselSlider.index - 2).slide,
		nextNext: carouselSlider.Components.Slides.getAt(carouselSlider.index + 2).slide,
	};
}

const carouselSlider = new Splide('[data-slider="carousel"]', {
	arrows: true,
	pagination: false,
	type: 'loop',
	autoplay: false,
	perPage: 5,
	focus: 'center',
	isNavigation: true,
	updateOnMove: true,
	breakpoints: {
		640: {
			perPage: 2,
		},
	}
});

carouselSlider.on( 'mounted', function () {
	const prevPrevSlide = getSlides(carouselSlider).prevPrev;
	const nextNextSlide = getSlides(carouselSlider).nextNext;
	prevPrevSlide.classList.add('is-prev-prev');
	nextNextSlide.classList.add('is-next-next');
} );

carouselSlider.on( 'move', function () {
	const prevPrevSlide = getSlides(carouselSlider).prevPrev;
	const nextNextSlide = getSlides(carouselSlider).nextNext;

	removeClasses(carouselSlider.Components.Elements.list.children);

	prevPrevSlide.classList.add('is-prev-prev');
	nextNextSlide.classList.add('is-next-next');
} );

